///////////////////////////////////////////////////////////////////////////////////////////
//frequentie omzetten naar fase increments
//in:  frequency
//out: phase increment
uint32_t AudioSynthWaveformOsc::f2phaseInc(float f)
{
	if (f > AUDIO_SAMPLE_RATE_EXACT / 2 || f < 0.0) return 0;
	return (f / AUDIO_SAMPLE_RATE_EXACT) * 4294967296.0f;
}
///////////////////////////////////////////////////////////////////////////////////////////
//een wavetable selecteren (2 dimensionele array)
//in:  harmonic order index (which lookup table to choose)
//in:  table
//out: void
//mod: pWavetable (array pointer)
void AudioSynthWaveformOsc::switchTable(uint8_t order, uint8_t table)
{
    switch(table)
    {
        case selectSinTable:
            pWaveTable = sinTable;
            break;
        case selectTriTable:
            pWaveTable = triTable[order];
            break;
        case selectSqrTable:
            pWaveTable = sqrTable[order];
            break;
        case selectSawTable:
            pWaveTable = sawTable[order];
            break;
    }
}
///////////////////////////////////////////////////////////////////////////////////////////
void AudioSynthWaveformOsc::setNote(int note, boolean glide)
{
    //copy midi, frequency and phase incremnt to old note (glide from note)
    fromMidiNote = note;
    midiNote = note;
    fromNoteFrequency = noteFrequency;
    fromNoteFrequency_phaseInc = noteFrequency_phaseInc;
    //set frequencies
    noteFrequency = nMidiFrequency[midiNote+cDetune] * detuneFrequency[fDetune+100] * detuneFrequency[fModulation+100]; 
    noteFrequency_phaseInc = f2phaseInc(noteFrequency);
    orderIndex = selectOrdertable[midiNote+cDetune];

    //to glide or not to glide?
    //if statements are here so the interrupts don't have to be interrupted for too long 
    uint32_t glideAccuSet;
    if(glide == false)
    {
        fromMidiNote = note;
        fromNoteFrequency = noteFrequency;
        fromNoteFrequency_phaseInc = noteFrequency_phaseInc;
        glideAccuSet = 0xFFFFFFFF;
    }
    else
    {
        glideAccuSet = 0;
    }    
    __disable_irq();
    phaseIncFromNote = fromNoteFrequency_phaseInc;
    phaseIncNote = noteFrequency_phaseInc;
    switchTable(orderIndex, selectTable);
    glideAccu = glideAccuSet;
    __enable_irq();
    
    DEBUG_PRINT1("noteFrequency", noteFrequency);
    DEBUG_PRINT1("fromNoteFrequency", fromNoteFrequency);
    DEBUG_PRINT1("phaseIncNote", phaseIncNote);
    DEBUG_PRINT1("phaseIncFromNote", phaseIncFromNote);
}
///////////////////////////////////////////////////////////////////////////////////////////
void AudioSynthWaveformOsc::toggleWaveTable()
{
    selectTable+=1;
    selectTable%=4;
}
void AudioSynthWaveformOsc::setWaveTable(int newTable)
{
    selectTable=newTable;
}
///////////////////////////////////////////////////////////////////////////////////////////
//detune:
void AudioSynthWaveformOsc::setfDetune(int cents)
{
    fDetune = cents;
    //nieuwe frequentie waar we naartoe gaan
    noteFrequency = nMidiFrequency[midiNote+cDetune] * detuneFrequency[fDetune+100] * detuneFrequency[fModulation+100]; 
    noteFrequency_phaseInc = f2phaseInc(noteFrequency);
    
    //alleen de phase increment wordt aangepast
    __disable_irq();
    phaseIncNote = noteFrequency_phaseInc;
    __enable_irq();
}
///////////////////////////////////////////////////////////////////////////////////////////
void AudioSynthWaveformOsc::setcDetune(int notes)
{
    cDetune = notes;
    
    noteFrequency = nMidiFrequency[midiNote+cDetune] * detuneFrequency[fDetune+100] * detuneFrequency[fModulation+100]; 
    noteFrequency_phaseInc = f2phaseInc(noteFrequency);
    orderIndex = selectOrdertable[midiNote+cDetune];
    
    __disable_irq();
    phaseIncFromNote = fromNoteFrequency_phaseInc;
    phaseIncNote = noteFrequency_phaseInc;
    switchTable(orderIndex, selectTable);
    __enable_irq();
}
///////////////////////////////////////////////////////////////////////////////////////////
//detunator: makes a supersaw effect by adding 2 extra 
void AudioSynthWaveformOsc::setDetunatorAmount(int value)
{
    detunatorAmount = value;
    detunatorMul[0] = detuneFrequency[100+value] * 2147483648;
    detunatorMul[1] = detuneFrequency[100+(value/2)-2] * 2147483648;
    detunatorMul[2] = detuneFrequency[100+(value/4)-3] * 2147483648;
    detunatorMul[3] = detuneFrequency[100-value] * 2147483648;
    detunatorMul[4] = detuneFrequency[100-(value/2)+1] * 2147483648;
    detunatorMul[5] = detuneFrequency[100-(value/4)+5] * 2147483648;
    __disable_irq();
    __enable_irq();
}
///////////////////////////////////////////////////////////////////////////////////////////
void AudioSynthWaveformOsc::setFMAmount(float value)
{
    int16_t newvalue = value * 32760.0;
    if(newvalue < 327)//a hunderth of fm is almost unhearable anyway
        newvalue = 0;
    __disable_irq();
    fmAmount = newvalue;
    __enable_irq();
    DEBUG_PRINT1("value",value);
    DEBUG_PRINT1("fmAmount",fmAmount);
}
///////////////////////////////////////////////////////////////////////////////////////////
//set the speed of the glide
void AudioSynthWaveformOsc::setGlideStep(float mSeconds)
{
    if(mSeconds < 1 || mSeconds > 20000) return;
    glideStep = 4294967295/(AUDIO_SAMPLE_RATE_EXACT*mSeconds*0.001);
}
///////////////////////////////////////////////////////////////////////////////////////////
uint32_t unsigned_multiply_32x32_rshift32(uint32_t a,uint32_t b)
{
    uint32_t o;
    o = ((uint64_t)a*b) >> 31;//was eerst 32. halveerde hierdoor de frequentie?
    return o;
}
///////////////////////////////////////////////////////////////////////////////////////////
void AudioSynthWaveformOsc::phaseIncGlide()
{
    if(glideAccu == 0xFFFFFFFF)
    {
        phaseInc = phaseIncNote;
        return;
    }
    uint32_t val1,val2;
    //glideAccu laten toenemen
    glideAccu+=glideStep;
    if(glideAccu < glideStep) //overflow
        glideAccu = 0xFFFFFFFF;
        
    val1 = unsigned_multiply_32x32_rshift32(0xFFFFFFFF-glideAccu, phaseIncFromNote);
    val2 = unsigned_multiply_32x32_rshift32(glideAccu, phaseIncNote);
    phaseInc = (val1>>1)+(val2>>1);
}
int16_t LinearSample(const int16_t *pWaveTable, uint32_t ph, int TableSizeBits, int32_t magnitude)
{
    uint32_t index,scale;
    int32_t val1, val2;
    
    index = ph >> (32-TableSizeBits);
    //lineaire interpolatie tussen het huidige en het volgende sampeltje
    val1 = pWaveTable[index];
    val2 = pWaveTable[index+1];
    scale = (ph >> TableSizeBits) & 0xFFFF;
    val2 *= scale;
    val1 *= 0xFFFF - scale;
    return multiply_32x32_rshift32(val1 + val2, magnitude);
}

void AudioSynthWaveformOsc::update(void)
{
    audio_block_t *block, *modInput;
    uint32_t i, ph, inc, index, scale;
    int32_t val1, val2, fminc, sample;
    int16_t mod;
    
    uint32_t dInc;
    uint32_t phd[6];
    modInput = receiveReadOnly();
    block = allocate();
    if (block) 
    {
    	ph = phase;
    	inc = phaseInc;
    
        for(int i = 0;i < 6; i++)
            phd[i] = phaseD[i];
        
            
        //no input routed: no fm / rm

        for (i=0; i < AUDIO_BLOCK_SAMPLES; i++) 
        {
            
	    sample = LinearSample(pWaveTable, ph, TableSizeBits, magnitude);
            //detunator: om supersaws te maken
            if(detunatorAmount > 0)
            {
                sample>>=2;
                for(int i = 0;i < 6; i++)
                {
                    dInc = ((uint64_t)inc*detunatorMul[i]) >> 31;
                    phd[i] += dInc;
                    sample += LinearSample(pWaveTable, phd[i], TableSizeBits, magnitude>>2);
                }
            }

            block->data[i] = sample;
            phaseIncGlide();
	    ph += inc;            

            if(modInput && fmAmount > 0)
            {
                mod = modInput->data[i];
                //mod = ((int32_t)mod*fmAmount)>>15;
                fminc = multiply_32x32_rshift32(inc, mod * fmAmount) << 1;
                for(int i = 0;i < 12; i++)
                    ph +=fminc;
            }
	}

        phase = ph;
        for(int i = 0;i < 6; i++)
            phaseD[i] = phd[i];
        
	transmit(block);
	release(block);
        release(modInput);
	return;
	}
    phase += phaseInc * AUDIO_BLOCK_SAMPLES;
}





